import java.util.Scanner;

public class Dragon
{
    private String curves[] = {"", "V","VVP", "VVPVVPP", "VVPVVPPVVVPPVPP", "VVPVVPPVVVPPVPPVVVPVVPPPVVPPVPP","VVPVVPPVVVPPVPPVVVPVVPPPVVPPVPPVVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPP",
    "VVPVVPPVVVPPVPPVVVPVVPPPVVPPVPPVVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPPVVVPVVPPVVVPPVPPVVVPVVPPPVVPPVPPPVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPP", "VVPVVPPVVVPPVPPVVVPVVPPPVVPPVPPVVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPPVVVPVVPPVVVPPVPPVVVPVVPPPVVPPVPPPVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPPVVVPVVPPVVPVVPPPVVPVVVPPVPPVVPVPPVVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPVPPVVVPPVVPVVPPPVVPVVVPPVPPVVPPPVPPVVPVVPPVVVPPVPPPVVPVVPPPVVPPVPP", "",
    "",
    "",
    "",
    ""};
    
    public String fc(int num) {
        int rem = num - 13;
        String be = curves[13];
        String e = curves[13];
        String t;

        for(int i = 0; i < rem; i++) {
            t = be;
            be = be + "V" + e;
            e = t + "P" + e;
        }

        return be;
    }
    
    
    
    public String findcurves(int num) {
        if (num >= 0 && num <= 13) {
            return curves[num];
        }else if(num > 13) {
            return fc(num);
        }else {
            return "";
        }

    }


    public static void main(String[] args) {
        Dragon d = new Dragon();

        Scanner c = new Scanner(System.in);

        int value = c.nextInt();


		System.out.println(d.findcurves(value));
	}
}